/* eslint-disable */
<template>
  <div>
    <header class="header">
      <b-container fluid>
        <b-row class="align-items-center">
          <b-col class="d-flex align-items-center">
            <img src="@/assets/logo.png" alt="Logo" class="logo" />
          </b-col>
          <b-col class="d-none d-md-flex justify-content-end">
            <b-button class="menu-button mr-2">
              <i class="fa-solid fa-cart-shopping"></i> Mis compras
            </b-button>
            <b-button class="menu-button mr-2">
              <i class="fa-solid fa-box"></i> Productos
            </b-button>
            <b-button class="menu-button mr-2" @click="toggleLogin">
              <i class="fa-solid fa-user"></i> Iniciar sesión
            </b-button>
          </b-col>
          <b-col class="d-md-none text-right">
            <b-button v-b-toggle.menu-collapse class="menu-toggle">
              <i class="fa-solid fa-bars"></i>
            </b-button>
          </b-col>
        </b-row>

        <b-collapse id="menu-collapse" class="d-md-none">
          <b-nav vertical>
            <b-nav-item>
              <b-button class="menu-button">
                <i class="fa-solid fa-cart-shopping"></i> Mis compras
              </b-button>
            </b-nav-item>
            <b-nav-item>
              <b-button class="menu-button">
                <i class="fa-solid fa-box"></i> Productos
              </b-button>
            </b-nav-item>
            <b-nav-item>
              <b-button class="menu-button" @click="toggleLogin">
                <i class="fa-solid fa-user"></i> Iniciar sesión
              </b-button>
            </b-nav-item>
          </b-nav>
        </b-collapse>
      </b-container>
    </header>

    <!-- Mostrar el componente InicioSesion si isLoginVisible es true -->
    <InicioSesion v-if="isLoginVisible" @close="toggleLogin" />
  </div>
</template>

<script>
import { BButton, BCollapse, BNav, BNavItem } from "bootstrap-vue-3";
import { vBToggle } from "bootstrap-vue-3";
import InicioSesion from "@/components/InicioSesion.vue";

export default {
  components: {
    BButton,
    BCollapse,
    BNav,
    BNavItem,
    InicioSesion,
  },
  directives: {
    "b-toggle": vBToggle,
  },
  data() {
    return {
      isLoginVisible: false,
    };
  },
  methods: {
    toggleLogin() {
      this.isLoginVisible = !this.isLoginVisible;
    },
  },
};
</script>

<style scoped>
.header {
  background-color: #0a641a;
  padding: 10px 0;
}

.logo {
  width: 180px;
}

.menu-button {
  font-weight: bold;
  color: #fff;
  background: #0a641a;
  border: none;
  font-size: 1.2rem;
  transition: color 0.3s ease;
}

.menu-button:hover {
  background: #0a641a;
  color: #ff9800;
}

.menu-button i {
  background: #0a641a;
  margin-right: 0.5rem;
  color: #ff9800;
}

.menu-toggle {
  font-size: 1.5rem;
  background: #0a641a;
  color: #ff9800;
  border: none;
  position: relative; /* Ajusta si el problema persiste */
  margin-right: -0.5rem; /* Ajusta el margen para acercar al borde */
}

.b-col.d-md-none.text-right {
  padding-right: 0; /* Elimina el relleno derecho */
  text-align: right; /* Asegura que el contenido esté alineado a la derecha */
}

.b-collapse {
  background-color: #0a641a;
  padding: 10px;
}

/* Responsive Styles */
@media (max-width: 768px) {
  .menu-button {
    width: 100%;
    background: #0a641a;
    text-align: left;
  }
}
</style>
