import "core-js/modules/es.array.push.js";
import axios from "axios";
export default {
  data() {
    return {
      carrito: [],
      totalCarrito: 0,
      id_cliente: null,
      metodo_pago_id: 1 // Valor predeterminado, ajusta si es necesario
    };
  },
  methods: {
    async cargarCarrito() {
      const token = localStorage.getItem("token");
      this.id_cliente = localStorage.getItem("id_cliente");
      if (!token) {
        alert("Por favor, inicie sesión para continuar.");
        return;
      }
      if (!this.id_cliente) {
        alert("El cliente no está definido.");
        return;
      }
      try {
        const response = await axios.post("http://localhost:3001/api/auth/carrito", {
          id_cliente: this.id_cliente,
          id_producto: this.carrito.map(item => item.id_producto),
          cantidad: this.carrito.map(item => item.cantidad),
          metodo_pago_id: this.metodo_pago_id
        }, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json"
          }
        });
        if (response.data.carrito) {
          this.carrito = response.data.carrito;
          this.actualizarTotal();
        } else {
          console.error("Respuesta inesperada:", response.data);
          alert("Error al cargar el carrito.");
        }
      } catch (error) {
        console.error("Error al cargar el carrito:", error);
        alert("Error al cargar el carrito.");
      }
    },
    async eliminarDelCarrito(index) {
      const token = localStorage.getItem("token");
      const producto = this.carrito[index];
      if (!token) {
        alert("Por favor, inicie sesión para continuar.");
        return;
      }
      if (!producto) {
        alert("El producto a eliminar no está definido.");
        return;
      }
      try {
        const response = await axios.delete(`http://localhost:3001/api/auth/carrito/${producto.id_carrito_producto}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        if (response.data.message === "Producto eliminado del carrito") {
          this.carrito.splice(index, 1);
          this.actualizarTotal();
        } else {
          alert("Error al eliminar el producto del carrito.");
        }
      } catch (error) {
        console.error("Error al eliminar del carrito:", error);
        alert("Error al eliminar el producto del carrito.");
      }
    },
    async procesarVenta() {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Por favor, inicie sesión para continuar.");
        return;
      }
      if (this.carrito.length === 0) {
        alert("Tu carrito está vacío.");
        return;
      }
      try {
        const response = await axios.post("http://localhost:3001/api/auth/carrito", {
          id_cliente: this.id_cliente,
          // Asegúrate de que este valor esté definido
          // Pasar los datos correctos de los productos aquí
          id_producto: this.carrito.map(item => item.id_producto),
          cantidad: this.carrito.map(item => item.cantidad),
          metodo_pago_id: this.metodo_pago_id // Ajusta si es necesario
        }, {
          headers: {
            Authorization: `Bearer ${token}`,
            // Asegúrate de que el token esté definido
            "Content-Type": "application/json" // Especifica el tipo de contenido
          }
        });
        if (response.data.message === "Venta procesada") {
          alert("Compra finalizada con éxito.");
          this.carrito = [];
          this.totalCarrito = 0;
          // Redirigir o hacer cualquier otra acción después de procesar la venta
          this.$router.push({
            name: "ConfirmacionCompra"
          });
        } else {
          alert("Error al procesar la venta.");
        }
      } catch (error) {
        console.error("Error al procesar la venta:", error);
        alert("Error al procesar la venta.");
      }
    },
    actualizarTotal() {
      this.totalCarrito = this.carrito.reduce((total, item) => {
        return total + item.precio_unitario * item.cantidad;
      }, 0);
    }
  },
  mounted() {
    this.cargarCarrito();
  }
};
